<template>
  <div class="mainBox">
    <div class="editorBox">
      <div class="editor-control" @click="format">
        <div v-if="config.bold" :class="'iconfont1 iconbold ' + (config.bold ? 'ql-active' : '')" data-name="bold"></div>
        <div v-if="config.fontSize" :class="'iconfont1 icontypeface ' + (config.fontSize === '24px' ? 'ql-active' : '')" data-name="fontSize" data-value="24px"></div>
        <div v-if="config.insertImage" class="iconfont1 iconimg" @click="insertImage"></div>
      </div>
      <editor
        id="editor"
        class="ql-container"
        placeholder="请记录下今天的心情.."
        bindready="onEditorReady"
        showImgSize
        showImgToolbar
        showImgResize>
      </editor>
    </div>
    <div class="expressionBox" @click="showExpression">
      <div class="happyBox" data-ex="happy">
        <div>开心</div>
      </div>
      <div class="sadBox" data-ex="sad">
        <div>伤心</div>
      </div>
      <div class="angryBox" data-ex="angry">
        <div>生气</div>
      </div>
    </div>
    <button @click="postContext" class="postBtn">提交</button>
  </div>
</template>

<script>

export default {
  onLoad(e){
    let id = e.id;
    console.log(e);
  },
  methods:{
    onEditorReady() {
      const that = this
      wx.createSelectorQuery().select('#editor').context(function (res) {
        that.editorCtx = res.context
      }).exec()
    },
    format (e) {
      console.log('format')
      console.log(e)
      let { name, value } = e.target.dataset
      if (!name) return
      this.editorCtx.format(name, value)
    },
    insertImage () {
      const that = this
      wx.chooseImage({
        count: 1,
        sizeType: ['original', 'compressed'],
        sourceType: ['album', 'camera'],
        success: function (res) {
          console.log(res.tempFilePaths);
          that.images.push(res.tempFilePaths[0]);
          that.editorCtx.insertImage({
            src: res.tempFilePaths[0],
            count: 1,
            width:"33%",
            height:"80",
            success: function (res) {
              
              console.log('insert image success')
            }
          });
          //uploadFile上传图片
        }
      });
    },
    showExpression(e){
      let that = this;
      // console.log(e.mp.target.dataset.ex);
      wx.showToast({
        title: '已选中', // 标题
        icon: 'success',  // 图标类型，默认success
        duration: 1500 , // 提示窗停留时间，默认1500ms
        success:function(){
          that.expression = e.mp.target.dataset.ex;
          // console.log(that.expression);
        }
      });
    },
    postContext(e){
      const that = this;
      
      this.editorCtx.getContents({
        success: res => {
          console.log(res);
        }
      });
    }
  },
  data: {
    config: {
      bold: true,
      fontSize: true,
      insertImage: true,
    },
    expression:null,
    images:[],
  },
  mounted () {
    // console.log('mounted')
    const that = this;
    wx.createSelectorQuery().select('#editor').context(function (res) {
      that.editorCtx = res.context
      // console.log(that.editorCtx);
    }).exec()
  }
}
</script>

